LD0 <- function(x,y) {

  n <- nchar(x)
  m <- nchar(y)

  M <- matrix(NA,n+1,m+1)

  M[1,] <- rep(0,m+1)
  M[,1] <- rep(0,n+1)

  for(i in 1:n){
    for(j in 1:m){

      if(substr(x,i,i)==substr(y,j,j)){
        M[i+1,j+1] <- M[i,j]
      } else {
        M[i+1,j+1] <- 1+min(M[i,j],M[i+1,j],M[i,j+1])
      }

    }
  }

  return(M[n+1,m+1])

}

